import React, { useState } from "react";
import { StyleSheet, View, Text, Button, FlatList, TouchableOpacity } from 'react-native';
import DocumentPicker, {types} from 'react-native-document-picker';
// This fucking thing is still not working. 

function UploadScreen({ navigation }) {
  const [selectedDocuments, setSelectedDocuments] = useState([]);

  const handleUpload = async () => {
    try {
      const results = await DocumentPicker.pickSingle({
        allowMultiSelection : true,
        type: [types.allFiles],
      });
      // Extract the selected document names
      console.log(res.name)
      const selectedNames = results.map((res) => res.name);
      setSelectedDocuments(selectedNames);
    } catch (error) {
      if (DocumentPicker.isCancel(error)) {
        // User canceled the picker
        console.log('User canceled document picker');
      } else {
        // Error occurred while picking documents
        console.log('Error selecting documents:', error);
      }
    }
  };

  const documents = [
    { id: 1, name: "Document 1.pdf" },
    { id: 2, name: "Document 2.docx" },
    { id: 3, name: "Document 3.txt" },
    // Add more dummy documents as needed
  ];

  const renderDocumentItem = ({ item }) => (
    <TouchableOpacity style={styles.documentItem}>
      <Text style={styles.documentText}>{item.name}</Text>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.headerText}>Upload your documents here!</Text>
      <Button title="Upload" onPress={handleUpload} />

      {/* Wrap the FlatList in a View with border styles */}
      <View style={styles.documentListContainer}>
        <Text style={styles.detailText}>List of Documents:</Text>
        <FlatList
          data={selectedDocuments.length > 0 ? selectedDocuments : documents}
          keyExtractor={(item) => item.id.toString()}
          renderItem={renderDocumentItem}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#fff',
  },
  headerText: {
    fontSize: 24,
    marginBottom: 16,
    textAlign: 'center',
  },
  detailText: {
    fontSize: 16,
    marginBottom: 8,
    fontWeight: 'bold',
  },
  documentListContainer: {
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 8,
    padding: 12,
    paddingTop: 14
  },
  documentItem: {
    padding: 12,
    marginBottom: 8,
    backgroundColor: '#F8F8F8',
    borderRadius: 8,
  },
  documentText: {
    fontSize: 14,
    color: '#333',
  },
});

export default UploadScreen;
