// This is the screen for the DocumentScreen. 
// We really should be using TypeScript, but oh well...
import React from "react";
// import DocumentPicker from "react-native-document-picker"
// This may be problematic, I may have to just modify it so it works. 
import { StyleSheet, View, Text, Button, FlatList } from 'react-native';

// Testing uploading an actual button first 
function UploadScreen({ navigation }) {
    // Dummy data for the list of documents
    const documents = [
      { id: 1, name: "Document 1.pdf" },
      { id: 2, name: "Document 2.docx" },
      { id: 3, name: "Document 3.txt" },
      // Add more dummy documents as needed
    ];

    return (
      <View style={styles.container}>
        <Text style={styles.headerText}>Upload your documents here!</Text>
        <Button title="Upload" onPress={() => console.log('Upload button pressed!')} />

        <Text style={styles.detailText}>List of Documents:</Text>

        {/* FlatList to display the list of documents */}
        <FlatList
          data={documents}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({ item }) => (
            <Text style={styles.documentText}>{item.name}</Text>
          )}
        />
      </View>
    );
  }

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 16,
      backgroundColor: '#fff',
      justifyContent: 'center',
      alignItems: 'center',
    },
    headerText: {
      fontSize: 24,
      marginBottom: 16,
      textAlign: 'center',
    },
    detailText: {
      fontSize: 16,
      marginBottom: 8,
      textAlign: 'center',
    },
    documentText: {
      fontSize: 14,
      marginBottom: 8,
    },
  });

  export default UploadScreen;
