import React, { useState } from 'react';
import { StyleSheet, View, Text, Image, TouchableOpacity, Dimensions, ScrollView } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { RFValue } from 'react-native-responsive-fontsize';

const GetStarted = () => {
  const navigation = useNavigation();
  const [currentIndex, setCurrentIndex] = useState(0);

  const data = [
    { header: 'Header 1', description: 'Description for header 1' },
    { header: 'Header 2', description: 'Description for header 2' },
    { header: 'Header 3', description: 'Description for header 3' },
    { header: 'Header 4', description: 'Description for header 4' },
  ];

  const handleSwipe = (index) => {
    setCurrentIndex(index);
  };

  return (
    <View style={styles.container}>
      <Image source={require('../../assets/getStarted-house.png')} style={styles.icon} />
      <ScrollView
        horizontal
        pagingEnabled
        showsHorizontalScrollIndicator={false}
        onScroll={(event) => {
          const newIndex = Math.round(event.nativeEvent.contentOffset.x / width);
          handleSwipe(newIndex);
        }}
        scrollEventThrottle={16}
      >
        {data.map((item, index) => (
          <View key={index} style={styles.textContainer}>
            <Text style={styles.header}>{item.header}</Text>
            <Text style={styles.description}>{item.description}</Text>
          </View>
        ))}
      </ScrollView>
      <View style={styles.bubbleContainer}>
        {data.map((_, index) => (
          <TouchableOpacity key={index} onPress={() => handleSwipe(index)} style={[styles.bubble, currentIndex === index && styles.activeBubble]} />
        ))}
      </View>
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('HomeScreen')}>
        <Text style={styles.buttonText}>Get Started</Text>
      </TouchableOpacity>
    </View>
  );
};

const { width, height } = Dimensions.get('window');

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#FFFF00',
  },
  icon: {
    width: width * 0.5,
    height: height * 0.2,
    resizeMode: 'contain',
    marginBottom: 20,
  },
  textContainer: {
    width,
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  header: {
    fontSize: RFValue(24, 680),
    fontWeight: 'bold',
    marginVertical: 20,
  },
  description: {
    fontSize: RFValue(16, 680),
    marginVertical: 20,
    textAlign: 'center',
  },
  bubbleContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    margin: 20,
  },
  bubble: {
    width: 10,
    height: 10,
    margin: 5,
    borderRadius: 5,
    backgroundColor: '#C0C0C0',
  },
  activeBubble: {
    backgroundColor: '#000',
  },
  button: {
    alignItems: 'center',
    justifyContent: 'center',
    padding: 15,
    borderRadius: 25,
    backgroundColor: '#000',
    width: width * 0.5,
    marginTop: 20,
  },
  buttonText: {
    color: '#fff',
    fontSize: RFValue(16, 680),
  },
});

export default GetStarted;
